import CSSProps from "../data/constants/CSSProps";

export class Base {
  SwitchPage(selectedPage) {
    this.SetSelectedPageButton(CSSProps.ID.Home, selectedPage);
    this.SetSelectedPageButton(CSSProps.ID.ServiceDesk, selectedPage);
    this.SetSelectedPageButton(CSSProps.ID.About, selectedPage);
    this.SetSelectedPageButton(CSSProps.ID.Dasboard, selectedPage);
    this.SetSelectedPageButton(CSSProps.ID.Tasks, selectedPage);
    this.SetSelectedPageButton(CSSProps.ID.Organisation, selectedPage);
    this.SetSelectedPageButton(CSSProps.ID.Setting, selectedPage);
  }

  SetSelectedPageButton(id, selectedPage) {
    const button = document.getElementById(id);
    if (button) {
      //button.style.cssText = "color : var(--primary);";
      document.getElementById("MyElement").classList.remove('fa-secondary');
     // alert("done");
    }

    if (selectedPage === id) {
      button.style.cssText = "color: red;";
    }
  }
}
