import "./Navbar.css";
import { ReactComponent as Home } from "../../icons/Home.svg";
import { ReactComponent as Theme } from "../../icons/Adjust.svg";
import { ReactComponent as About } from "../../icons/About.svg";
import { ReactComponent as Helpdesk } from "../../icons/Helpdesk.svg";
import CSSProps from "../../data/constants/CSSProps";
import DictionaryProps from "../../data/constants/DictionaryProps";
import LocalStorageProps from "../../data/constants/LocalStorageProps";
import React, { useEffect } from "react";
import { useTranslation } from "react-i18next";
import LoginControl from "./controls/LoginControl"
import Paths from "../../data/constants/Paths";
import ThemeControl from "./controls/ThemeControl";

function HeaderComponent() {
  const [t, i18n] = useTranslation("common");
  return (
    <div>
      <h1>{t(DictionaryProps.Greetings.Welcome, { name: "Jhon Doe" })}</h1>
      <button onClick={() => i18n.changeLanguage("nl")}>nl</button>
      <button onClick={() => i18n.changeLanguage("en")}>en</button>
    </div>
  );
}

const Navbar = () => {
  const [t, i18n] = useTranslation("common");
  var alterToggleName = "Alternative"; 
  useEffect(() => {
    // DOM Elements

    const darkButton = document.getElementById(CSSProps.Dark);
    const lightButton = document.getElementById(CSSProps.Light);
    const solarButton = document.getElementById(CSSProps.Alternative);
    const body = document.body;

    // Apply the cached theme on reload

    const theme = localStorage.getItem(LocalStorageProps.Theme);
    const isSolar = localStorage.getItem(LocalStorageProps.IsSolar);

    if (theme) {
      body.classList.add(theme);
      isSolar && body.classList.add(CSSProps.Alternative);
    }

    if (isSolar) {
      solarButton.style.cssText =CSSProps.BGWhite;
      solarButton.innerText = t(DictionaryProps.Normalize);
    }

    // Button Event Handlers

    darkButton.onclick = () => {
      body.classList.replace(CSSProps.Light, CSSProps.Dark);
      localStorage.setItem(LocalStorageProps.Theme, CSSProps.Dark);

      if (body.classList.contains(CSSProps.Alternative)) {
        solarButton.style.cssText = CSSProps.BGDark;
      } else {
        solarButton.style.cssText = CSSProps.BGPrimary;
      }
    };

    lightButton.onclick = () => {
      body.classList.replace(CSSProps.Dark, CSSProps.Light);
      localStorage.setItem(LocalStorageProps.Theme, CSSProps.Light);

      if (body.classList.contains(CSSProps.Alternative)) {
        solarButton.style.cssText = CSSProps.BGWhite;
      } else {
        solarButton.style.cssText = CSSProps.BGPrimary;
      }
    };

    solarButton.onclick = () => {
      if (body.classList.contains(CSSProps.Alternative)) {
        body.classList.remove(CSSProps.Alternative);
        solarButton.style.cssText = CSSProps.BGPrimary;
        solarButton.innerText = t(DictionaryProps.Alternative);
        localStorage.removeItem(LocalStorageProps.IsSolar);
      } else {
        if (body.classList.contains(CSSProps.Light)) {
          solarButton.style.cssText = CSSProps.BGWhite;
        } else {
          solarButton.style.cssText = CSSProps.BGDark;
        }
        body.classList.add(CSSProps.Alternative);
        solarButton.innerText = t(DictionaryProps.Normalize);

        localStorage.setItem(LocalStorageProps.IsSolar, true);
      }
    };
  }, []);

  
  return (
    <nav className="navbar">
      <ul className="navbar-nav">
        <li className="navbar-item">
          <a className="nav-link" href={Paths.Default}>
            <Home className="nav-icon"></Home>
            <span className="navlink-text">Home</span>
          </a>
        </li>

        <ThemeControl/>
        <li className="navbar-item">
          <a className="nav-link" href={Paths.Default}>
            <Helpdesk className="nav-icon"></Helpdesk>
            <span className="navlink-text">Helpdesk</span>
          </a>
        </li>
        <li className="navbar-item">
          <a className="nav-link" href={Paths.Default}>
            <About className="nav-icon"></About>
            <span className="navlink-text">About</span>
          </a>
        </li>
        <LoginControl/>
      </ul>
    </nav>
  );
};

export default Navbar;
