import "./Sidebar.css";
import { ReactComponent as Arrow } from "../../icons/Arrow.svg";
import { ReactComponent as Dashboard } from "../../icons/Dashboard.svg";
import { ReactComponent as Settings } from "../../icons/Settings.svg";
import { ReactComponent as Tasks } from "../../icons/Tasks.svg";
import { ReactComponent as Sitemap } from "../../icons/Sitemap.svg";
import Paths from "../../data/constants/Paths";
import CSSProps from "../../data/constants/CSSProps";
import DictionaryProps from "../../data/constants/DictionaryProps";
import AppSettings from "../../data/AppSettings";

const Sidebar = () => {
  const [t] = useTranslation(AppSettings.TranslationFilename);

  return (
    <nav className={CSSProps.SideBar.Bar}>
      <ul className={CSSProps.SideBar.Navigation}>
        <li className={CSSProps.SideBar.Logo}>
          <a href={Paths.None} className={CSSProps.SideBar.Link}>
            <span className={CSSProps.SideBar.LinkText + CSSProps.SideBar.LogoText}>{AppSettings.ApplicationName}</span>
            <Arrow />
          </a>
        </li>
        <li className={CSSProps.SideBar.Item}>
          <a href={Paths.None} className={CSSProps.SideBar.Link}>
            <Dashboard/>
            <span className={CSSProps.SideBar.LinkText}>{t(DictionaryProps.Dashboard)}</span>
          </a>
        </li>
        <li className={CSSProps.SideBar.Item}>
          <a href={Paths.None} className={CSSProps.SideBar.Link}>
            <Tasks/>
            <span className={CSSProps.SideBar.LinkText}>{t(DictionaryProps.Tasks)}</span>
          </a>
        </li>
        <li className={CSSProps.SideBar.Item}>
          <a href={Paths.None} className={CSSProps.SideBar.Link}>
            <Sitemap/>
            <span className={CSSProps.SideBar.LinkText}>{t(DictionaryProps.Organisation)}</span>
          </a>
        </li>
        <li className={CSSProps.SideBar.Item}>
          <a href={Paths.None} className={CSSProps.SideBar.Link}>
            <Settings/>
            <span className={CSSProps.SideBar.LinkText}>{t(DictionaryProps.Settings)}</span>
          </a>
        </li>
      </ul>
    </nav>
  );
};

export default Sidebar;
