import "./Sidebar.css";
import { ReactComponent as Arrow } from "../../icons/Arrow.svg";
import Paths from "../../data/constants/Paths";
import CSSProps from "../../data/constants/CSSProps";
import DictionaryProps from "../../data/constants/DictionaryProps";
import AppSettings from "../../data/AppSettings";
import { useTranslation } from "react-i18next";
import { Base } from "../Base";
import ClickSound from "../../audio/mixkit-mouse-click-close-1113.wav";

const Sidebar = () => {
  const [t] = useTranslation(AppSettings.TranslationFilename);

  return (
    <nav className={CSSProps.SideBar.Bar}>
      <ul className={CSSProps.SideBar.Navigation}>
        <li className={CSSProps.SideBar.Logo} >
          <a href={Paths.None} className={CSSProps.SideBar.Link}>
            <span className={CSSProps.SideBar.LinkText + CSSProps.SideBar.LogoText}>{AppSettings.ApplicationName}</span>
            <Arrow />
          </a>
        </li>
        <NavItem path={Paths.Default} text={t(DictionaryProps.Dashboard)} onClickPageID={CSSProps.ID.Dashboard} iconID={CSSProps.ID.Dashboard}/>
        <NavItem path={Paths.Default} text={t(DictionaryProps.Tasks)} onClickPageID={CSSProps.ID.Tasks} iconID={CSSProps.ID.Tasks}/>
        <NavItem path={Paths.Default} text={t(DictionaryProps.Organisation)} onClickPageID={CSSProps.ID.Organisation} iconID={CSSProps.ID.Organisation}/>
        <NavItem path={Paths.Default} text={t(DictionaryProps.Settings)} onClickPageID={CSSProps.ID.Settings} iconID={CSSProps.ID.Settings}/>
      </ul>
    </nav>
  );
};

export default Sidebar;
