import "../Navbar.css";
import DictionaryProps from "../../../data/constants/DictionaryProps";
import LocalStorageProps from "../../../data/constants/LocalStorageProps";
import React from "react";
import { useTranslation } from "react-i18next";
import Language from "../../../icons/Language.svg";
import Paths from "../../../data/constants/Paths";
const LanguageControl = () => {
  const [t, i18n] = useTranslation("common");
  return (
    <li className="navbar-item has-dropdown">
      <a className="nav-link" href={Paths.Default}>
        <Language className="nav-icon"/>
        <span className="navlink-text">Theme</span>
      </a>
      <ul className="dropdown">
        <li className="dropdown-item">
          <a id="light" href={Paths.Default}>
            <span className="dropdown-text">NL</span>
          </a>
        </li>
        <li className="dropdown-item">
          <a id="dark" href={Paths.Default}>
            <span className="dropdown-text">EN</span>
          </a>
        </li>
      </ul>
    </li>
  );
};

function CacheLanguage(language) {
  localStorage.removeItem(LocalStorageProps.Language);
  localStorage.setItem(LocalStorageProps.Language, language);
}
export default LanguageControl;
