import CSSProps from "../data/constants/CSSProps";
export class Base {
  SwitchPage(selectedPage) {
    SetSelectedPageButton(CSSProps.ID.Home, selectedPage);
  }

  SetSelectedPageButton(id) {
    const button = document.getElementById(id);
    button.style.cssText = "color : var(--primary);";
    if (selectedPage === id) {
      button.style.cssText = "color: red;";
    }
  }
}
