import "./Navbar.css";
import { ReactComponent as Home } from "../../icons/Home.svg";
import { ReactComponent as Login } from "../../icons/Profile.svg";
import { ReactComponent as Theme } from "../../icons/Adjust.svg";
import { ReactComponent as About } from "../../icons/About.svg";
import { ReactComponent as Helpdesk } from "../../icons/Helpdesk.svg";

import React, { useEffect } from "react";

const Navbar = () => {
  useEffect(() => {
    // DOM Elements

    const darkButton = document.getElementById("dark");
    const lightButton = document.getElementById("light");
    const solarButton = document.getElementById("solar");
    const body = document.body;

    // Apply the cached theme on reload

    const theme = localStorage.getItem("theme");
    const isSolar = localStorage.getItem("isSolar");

    if (theme) {
      body.classList.add(theme);
      isSolar && body.classList.add("solar");
    }

    // Button Event Handlers

    darkButton.onclick = () => {
      body.classList.replace("light", "dark");
      localStorage.setItem("theme", "dark");

      if (body.classList.contains("solar")) {
        solarButton.style.cssText = `
        --bg-solar: #2a2e35;
  `;
      } else {
        solarButton.style.cssText = `
        --bg-solar: var(--primary);
      `;
      }
    };

    lightButton.onclick = () => {
      body.classList.replace("dark", "light");
      localStorage.setItem("theme", "light");

      if (body.classList.contains("solar")) {
        solarButton.style.cssText = `
        --bg-solar: white;
  `;
      } else {
        solarButton.style.cssText = `
        --bg-solar: var(--primary);
      `;
      }
    };

    solarButton.onclick = () => {
      if (body.classList.contains("solar")) {
        body.classList.remove("solar");
        solarButton.style.cssText = `--bg-solar: var(--primary);`;

        solarButton.innerText = "Alternative";

        localStorage.removeItem("isSolar");
      } else {
        if (body.classList.contains("light")) {
          solarButton.style.cssText = `--bg-solar: white;`;
        } else {
          solarButton.style.cssText = `--bg-solar: #2a2e35;`;
        }
        body.classList.add("solar");
        solarButton.innerText = "Normalize";

        localStorage.setItem("isSolar", true);
      }
    };
  }, []);

  let url = "#";
  return (
    <nav className="navbar">
      <ul className="navbar-nav">
        <li className="navbar-item">
          <a className="nav-link" href={url}>
            <Home className="nav-icon"></Home>
            <span className="navlink-text">Home</span>
          </a>
        </li>

        <li className="navbar-item">
          <a className="nav-link" href={url}>
            <Login className="nav-icon"></Login>
            <span className="navlink-text">Login</span>
          </a>
        </li>

        <li className="navbar-item has-dropdown">
          <a className="nav-link" href={url}>
            <Theme className="nav-icon"></Theme>
            <span className="navlink-text">Theme</span>
          </a>
          <ul className="dropdown">
            <li className="dropdown-item">
              <a id="light" href={url}>
                <span className="dropdown-text">Light</span>
              </a>
            </li>
            <li className="dropdown-item">
              <a id="dark" href={url}>
                <span className="dropdown-text">Dark</span>
              </a>
            </li>
            <li className="dropdown-item">
              <a id="solar" href={url}>
                <span className="dropdown-text">Normalize</span>
              </a>
            </li>
          </ul>
        </li>
        <li className="navbar-item">
          <a className="nav-link" href={url}>
            <Helpdesk className="nav-icon"></Helpdesk>
            <span className="navlink-text">Helpdesk</span>
          </a>
        </li>
        <li className="navbar-item">
          <a className="nav-link" href={url}>
            <About className="nav-icon"></About>
            <span className="navlink-text">About</span>
          </a>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
