import "./Navbar.css";
import { ReactComponent as Home } from "../../icons/Home.svg";
import { ReactComponent as About } from "../../icons/About.svg";
import { ReactComponent as Helpdesk } from "../../icons/Helpdesk.svg";
import DictionaryProps from "../../data/constants/DictionaryProps";
import React from "react";
import { useTranslation } from "react-i18next";
import LoginControl from "./controls/LoginControl";
import Paths from "../../data/constants/Paths";
import ThemeControl from "./controls/ThemeControl";
import LanguageControl from "./controls/LanguageControl";
import CSSProps from "../../data/constants/CSSProps";
import AppSettings from "../../data/AppSettings";
import { Base } from "../Base";
import useSound from "use-sound";
import boopSfx from "../../audio/mixkit-mouse-click-close-1113.wav";
const Navbar = () => {
  const [t] = useTranslation(AppSettings.TranslationFilename);
  const [play] = useSound(boopSfx);
  
  return (
    <nav className={CSSProps.NavBar.Bar}>
      <ul className={CSSProps.NavBar.Navigation}>
        <li className={CSSProps.NavBar.Item} onClick={()=>{Base.prototype.SwitchPage(CSSProps.ID.Home); play();}}>
          <a className={CSSProps.NavBar.Link} href={Paths.Default}>
            <Home id={CSSProps.ID.Home} className={CSSProps.NavBar.Icon}></Home>
            <span className={CSSProps.NavBar.Text}>{t(DictionaryProps.Home)}</span>
          </a>
        </li>
        <ThemeControl onClick={play}/>
        <li className={CSSProps.NavBar.Item} onClick={()=>{Base.prototype.SwitchPage(CSSProps.ID.ServiceDesk);play();}}>
          <a className={CSSProps.NavBar.Link} href={Paths.Default}>
            <Helpdesk id={CSSProps.ID.ServiceDesk} className={CSSProps.NavBar.Icon}></Helpdesk>
            <span className={CSSProps.NavBar.Text}>{t(DictionaryProps.Helpdesk)}</span>
          </a>
        </li>
        <LanguageControl onClick={play}/>
        <li className={CSSProps.NavBar.Item} onClick={()=>{Base.prototype.SwitchPage(CSSProps.ID.About);play();}}>
          <a className={CSSProps.NavBar.Link} href={Paths.Default}>
            <About id={CSSProps.ID.About} className={CSSProps.NavBar.Icon}></About>
            <span className={CSSProps.NavBar.Text}>{t(DictionaryProps.About)}</span>
          </a>
        </li>
        <LoginControl onClick={()=>{Base.prototype.SwitchPage(CSSProps.ID.Login);play();}}/>
      </ul>
    </nav>
  );
};

export default Navbar;
