import "../Navbar.css";
import DictionaryProps from "../../../data/constants/DictionaryProps";
import LocalStorageProps from "../../../data/constants/LocalStorageProps";
import React, { useEffect } from "react";
import { useTranslation } from "react-i18next";
import { ReactComponent as Language } from "../../../icons/Language.svg";
import Paths from "../../../data/constants/Paths";
import CSSProps from "../../../data/constants/CSSProps";
import AppSettings from "../../../data/AppSettings";
import { Validator } from "../../../Tools/Validator";

const LanguageControl = () => {
  const [t, i18n] = useTranslation(AppSettings.TranslationFilename);
  useEffect(()=>{
    CacheLanguage(null);
  });
  return (
    <li className={CSSProps.NavBar.Item + CSSProps.NavBar.hasDropdown}>
      <a className={CSSProps.NavBar.Link} href={Paths.None}>
        <Language className={CSSProps.NavBar.Icon} />
        <span className={CSSProps.NavBar.Text}>
          {t(DictionaryProps.Language)}
        </span>
      </a>
      <ul className={CSSProps.NavBar.Dropdown}>
        <li
          id={AppSettings.NL}
          className={CSSProps.NavBar.DropdownItem}
          onClick={() => {
            i18n.changeLanguage(AppSettings.NL);
            CacheLanguage(AppSettings.NL);
          }}
        >
          <a href={Paths.None}>
            <span className={CSSProps.NavBar.DropdownText}>Nederlands</span>
          </a>
        </li>
        <li
          id={AppSettings.EN}
          className={CSSProps.NavBar.DropdownItem}
          onClick={() => {
            i18n.changeLanguage(AppSettings.EN);
            CacheLanguage(AppSettings.EN);
          }}
        >
          <a href={Paths.None}>
            <span className={CSSProps.NavBar.DropdownText}>English</span>
          </a>
        </li>
      </ul>
    </li>
  );
};

function CacheLanguage(language) {
  let selectedLanguage = localStorage.getItem(LocalStorageProps.Language);

  if (Validator.prototype.isEmpty(selectedLanguage) === true && Validator.prototype.isEmpty(language) === true) {
    language = AppSettings.EN;
    
  }
  
  if (Validator.prototype.isEmpty(selectedLanguage) === false && Validator.prototype.isEmpty(language) === true) {
    language = selectedLanguage;
    
  }

  if (Validator.prototype.isEmpty(selectedLanguage) === false) {
    const selectedButton = document.getElementById(selectedLanguage);
    selectedButton.style.cssText = "";
  }

  const newSelectedButton = document.getElementById(language);

  newSelectedButton.style.cssText = "border-bottom: 2px solid var(--primary);";

  localStorage.removeItem(LocalStorageProps.Language);
  localStorage.setItem(LocalStorageProps.Language, language);
}
export default LanguageControl;
