import "../Navbar.css";
import CSSProps from "../../../data/constants/CSSProps";
import React from "react";
import { SVGIcon } from "../../Icon";
import ClickSound from "../../../audio/mixkit-mouse-click-close-1113.wav";
import LocalStorageProps from "../../../data/constants/LocalStorageProps";
import { Validator } from "../../../tools/Validator";

class Volume extends React.Component {
  getIcon() {
    let cachedValue = localStorage.getItem(LocalStorageProps.Volume);
    if (Validator.prototype.isEmpty(cachedValue)) {
      localStorage.setItem(LocalStorageProps.Volume, CSSProps.NavBar.VolumeOn);
      return localStorage.getItem(LocalStorageProps.Volume);
    }
    return cachedValue;
  }

  toggle() {
    let currentState = this.getIcon();
    const btnIcon = document.getElementById(CSSProps.ID.Volume);

    if(currentState === CSSProps.NavBar.VolumeOn){
      localStorage.setItem(LocalStorageProps.Volume, CSSProps.NavBar.VolumeOff);
      btnIcon.style.cssText= CSSProps.Style.NavDeselected;     
    }else{
      localStorage.setItem(LocalStorageProps.Volume, CSSProps.NavBar.VolumeOn); 
      btnIcon.style.cssText= CSSProps.Style.NavSelected;     
    }
    
    let toggleState =localStorage.getItem(LocalStorageProps.Volume);
    this.setState({iconName : toggleState});
    return toggleState;
  }

  render() {
    
    let iconName;
    let inLineStyling ="{color : var(--primary);}"
    if(this.state != null){
      if(Validator.prototype.isEmpty(this.state.iconName) === false){
        iconName = this.state.iconName;
      }
    }else{
      iconName = this.getIcon();
    }
    
    return (
      <li className={CSSProps.NavBar.Item} onClick={() => iconName =this.toggle()}>
        <a href={this.props.path} className={CSSProps.NavBar.Link}>
          <SVGIcon
            name={iconName}
            id={CSSProps.ID.Volume}
            className={CSSProps.NavBar.Icon}
            style={inLineStyling}
          />
          <span className={CSSProps.NavBar.Text}>{this.props.text}</span>
          <audio id={this.props.iconID + CSSProps.ID.Audio} src={ClickSound} />
        </a>
      </li>
    );
  }
}

export default Volume;
