import "./Navbar.css";
import DictionaryProps from "../../data/constants/DictionaryProps";
import React from "react";
import { useTranslation } from "react-i18next";
import LoginControl from "./controls/LoginControl";
import Paths from "../../data/constants/Paths";
import ThemeControl from "./controls/ThemeControl";
import LanguageControl from "./controls/LanguageControl";
import CSSProps from "../../data/constants/CSSProps";
import AppSettings from "../../data/AppSettings";
import { Base } from "../Base";
import ClickSound from "../../audio/mixkit-mouse-click-close-1113.wav";
import NavItem from "./controls/NavItem";
import  { Icon}  from "../Icon";

const Navbar = () => {
  const [t] = useTranslation(AppSettings.TranslationFilename);
  
  return (

    <nav className={CSSProps.NavBar.Bar}>
      <ul className={CSSProps.NavBar.Navigation}>
        <li className={CSSProps.NavBar.Item} onClick={()=>Base.prototype.SwitchPage(CSSProps.ID.Home)}>
          <a className={CSSProps.NavBar.Link} href={Paths.Default}>
            <Icon name={CSSProps.ID.Home} id={CSSProps.ID.Home} class={CSSProps.NavBar.Icon}></Icon>
            <span className={CSSProps.NavBar.Text}>{t(DictionaryProps.Home)}</span>
            <audio id={CSSProps.ID.Home + CSSProps.ID.Audio} src={ClickSound}/>
          </a>
        </li>
        <ThemeControl/>
        <li className={CSSProps.NavBar.Item} onClick={()=>Base.prototype.SwitchPage(CSSProps.ID.ServiceDesk)}>
          <a className={CSSProps.NavBar.Link} href={Paths.Default}>
            <Icon name={CSSProps.ID.ServiceDesk} id={CSSProps.ID.ServiceDesk} className={CSSProps.NavBar.Icon}></Icon>
            <span className={CSSProps.NavBar.Text}>{t(DictionaryProps.Helpdesk)}</span>
            <audio id={CSSProps.ID.ServiceDesk + CSSProps.ID.Audio} src={ClickSound}/>
          </a>
        </li>
        <LanguageControl/>
        <li className={CSSProps.NavBar.Item} onClick={()=>Base.prototype.SwitchPage(CSSProps.ID.About)}>
          <a className={CSSProps.NavBar.Link} href={Paths.Default}>
            <Icon name={CSSProps.ID.About} id={CSSProps.ID.About} className={CSSProps.NavBar.Icon}></Icon>
            <span className={CSSProps.NavBar.Text}>{t(DictionaryProps.About)}</span>
            <audio id={CSSProps.ID.About + CSSProps.ID.Audio} src={ClickSound}/>
          </a>
        </li>
        <NavItem path={Paths.Default} text={t(DictionaryProps.About)} onClickPageID={CSSProps.ID.About} IconID={CSSProps.ID.About}></NavItem>
        <LoginControl onClick={()=>Base.prototype.SwitchPage(CSSProps.ID.Login)}/>
        
      </ul>
    </nav>
  );
};

export default Navbar;
